mapper.select:
select
  id, name, icon, title, parent_id, parent_title, path, is_menu, client_route, is_cache, need_login, encode, sort, version
from iotbj.page


        and id = #{id}


        and name = #{name}


        and icon = #{icon}


        and title = #{title}


        and parent_id = #{parentId}


        and parent_title = #{parentTitle}


        and path = #{path}


        and is_menu = #{isMenu}


        and client_route = #{clientRoute}


        and is_cache = #{isCache}


        and need_login = #{needLogin}


        and encode = #{encode}


        and sort = #{sort}


        and version = #{version}
